<?php/** * Description of Instructor * * @author aparagas */class Instructor extends AppModel{         public $hasMany = array(        'Course' => array(            'className' => 'Course',            'foreignKey' => 'id'        )    );       var $virtualFields = array(        'full_name' => 'CONCAT(Instructor.first_name, " ", Instructor.last_name)'    );            var $validate = array(        'id' => array(            'minLength' => array(                'rule' => array('minLength', 7),                'message' => 'Panther ID must be 7 characters long.'            ),            'maxLength' => array(                'rule' => array('maxLength', 7),                'message' => 'Panther ID must be 7 characters long.'            ),            'numeric' => array(                'rule' => 'numeric',                'message' => 'Must be numeric.'            )        ),        'last_name' => array(            'notEmpty' => array(                'rule' => 'notEmpty',                'message' => 'Last Name cannot be empty.'            )        ),        'first_name' => array(            'notEmpty' => array(                'rule' => 'notEmpty',                'message' => 'First Name cannot be empty.'            )        ),        'email' => array(            'notEmpty' => array(                'rule' => 'notEmpty',                'message' => 'First Name cannot be empty.'            ),            'email' => array(                'rule' => array('email', true),                'message' => 'Email address not valid.'            ),            'isUnique' => array(                'rule' => 'isUnique',                'message' => 'Email must be unique'            )        )    );    public function instructorExists($instructorName){        return $this->multiColumnSearch($instructorName, 'first_name', 'last_name');    }}